// Color constants matching the design
export const COLORS = {
  primary: '#1a1f3a', // Dark navy blue background
  secondary: '#8b8f47', // Olive/khaki color for circles
  white: '#ffffff',
  black: '#000000',
  gray: '#666666',
  lightGray: '#f5f5f5',
  transparent: 'transparent',
};

// Font sizes
export const FONT_SIZES = {
  small: 12,
  medium: 16,
  large: 20,
  xlarge: 24,
  xxlarge: 32,
};

// Spacing
export const SPACING = {
  xs: 4,
  sm: 8,
  md: 16,
  lg: 24,
  xl: 32,
  xxl: 48,
};

// Screen dimensions helpers
export const SCREEN_PADDING = SPACING.md;

// Authentication button styles
export const BUTTON_HEIGHT = 48;
export const BORDER_RADIUS = 24;
